  getTodayDate(): string {
    const today = new Date();
    const year = today.getFullYear();
    const month = today.getMonth() + 1; // Months are zero-based
    const day = today.getDate();

    return `${year}-${this.padZero(month)}-${this.padZero(day)}`;
  }

  padZero(num: number): string {
    return num < 10 ? '0' + num : num.toString();
  }
  ngOnInit(): void {
    this.class=''
    localStorage.removeItem('email');
    this.at.isLoggedIn = false;
    this.step1 = true;
    this.form = new FormGroup({
      nome: new FormControl(null, Validators.required),
      cognome: new FormControl(null, Validators.required),
      email: new FormControl(null, [Validators.required, Validators.email]),
      dataDiNascita: new FormControl(null, Validators.required),
      luogoDiNascita: new FormControl(null, Validators.required),
      sesso: new FormControl(null, Validators.required),
      interessi: new FormControl(null, Validators.required),
      professione: new FormControl(null, Validators.required),
      stato: new FormControl(null, Validators.required),
      avatar: new FormControl(null, Validators.required),
      password: new FormControl(null, Validators.required),
      passwordCheck:new FormControl(null,Validators.required),
      immagineCaricata: new FormControl(''),
    });

  }


  avanti() {
    if (this.step1 && !this.step2 && !this.step3) {
      this.step1 = false;
      this.step2 = true;
    } else if (
      this.form.value.sesso &&
      !this.step1 &&
      this.step2 &&
      !this.step3
    ) {
      this.step2 = false;
      this.step3 = true;
      this.form.controls['immagineCaricata'].setValue('')
      this.form.controls['avatar'].setValue(null)
    }
  }

  message() {
    if (!this.form.value.sesso) {
      const dialogRef = this.dialog.open(DialogComponent, {
        width: '400px',
        height: '400px',
        data: [
          'errore',
          'Controlla bene! Devi selezionare il sesso prima di continuare.',
        ],
      });
      dialogRef.afterClosed().subscribe(() => {});
    }
  }
  indietro() {
    if (!this.step1 && this.step2 && !this.step3) {
      localStorage.clear();
      this.step1 = true;
      this.step2 = false;
    } else if (!this.step1 && !this.step2 && this.step3) {
      this.step2 = true;
      this.step3 = false;
    }
  }

  onSbmt() {
    if (this.variableImg != '' && this.variableImg != null) {
      this.form.controls['avatar'].setValue(this.variableImg);
      this.form.controls['avatar'].updateValueAndValidity();
    } else if (this.variableImg == '' || this.variableImg == null) {
      this.form.controls['avatar'].addValidators(Validators.required);
      this.form.controls['avatar'].updateValueAndValidity();
    }

    if (this.form.valid && this.form.controls['password'].value==this.form.controls['passwordCheck'].value) {
      this.at
        .signUp({
          email: this.form.value.email,
          password: this.form.value.password,
          returnSecureToken: true,
        })
        .subscribe((data) => {});

      this.ss
        .insertUser({
          nome: this.form.value.nome,
          cognome: this.form.value.cognome,
          email: this.form.value.email,
          dataDiNascita: this.form.value.dataDiNascita,
          luogoDiNascita: this.form.value.luogoDiNascita,
          sesso: this.form.value.sesso,
          interessi: this.form.value.interessi,
          professione: this.form.value.professione,
          stato: this.form.value.stato,
          avatar: this.form.value.avatar,
          password: this.form.value.password,
          isOnline: false,
        })
        .subscribe((data: any) => {
          if (data) {
            this.at.isLoggedIn = true;
            this.ss
              .modifyUser(data.name, {
                id: data.name,
              })
              .subscribe((data: any) => {
                this.setLocal(this.form);
                setTimeout(() => {
                  this.spinner.hide();
                }, 1000);
                this.ss.postVisitatori({ visitor: 1 }).subscribe((data) => {
                  this.router.navigate(['/fatherOfAll']);
                });
              });
          }
        });
    } else {
      const dialogRef = this.dialog.open(DialogComponent, {
        data: [
          'errore',
          'Controlla bene! Avresti potuto lasciare qualche campo vuoto o invalido.',
        ],
        width: '400px',
        height: '400px',
      });
      dialogRef.afterClosed().subscribe((result: any) => {});
    }
  }

  viewFileValue(a: any) {
    if (a.target.files && a.target.files[0]) {
      var reader = new FileReader();

      reader.onload = (event: ProgressEvent) => {
        this.variableImg = (<FileReader>event.target).result;
        this.cdr.detectChanges();
      };
      reader.readAsDataURL(a.target.files[0]);
    }
  }
  setLocal(persona: any) {
    localStorage.setItem('avatar', persona.value.avatar);
    localStorage.setItem('email', persona.value.email);
    localStorage.setItem('dataDiNascita', persona.value.dataDiNascita);
    localStorage.setItem('luogoDiNascita', persona.value.luogoDiNascita);
    localStorage.setItem('interessi', persona.value.interessi);
    localStorage.setItem('stato', persona.value.stato);
    localStorage.setItem('sesso', persona.value.sesso);
    localStorage.setItem('isOnline', persona.value.isOnline);
    localStorage.setItem('id', persona.value.id);
    localStorage.setItem('nome', persona.value.nome);
    localStorage.setItem('cognome', persona.value.cognome);
  }



checkPassword(value:string,value1:string){
  if(value!=''&&value1!=''&&value!=null&&value1!=null){
    if(value==value1){
      this.class='border-success'
    }else{
      this.class='border-danger'
    }
  }else{
    this.class=''
  }
}
